namespace Mageplaza\PdfInvoice\Model\Api\Data;

use Magento\Framework\Model\AbstractExtensibleModel;
use Mageplaza\PdfInvoice\Api\Data\ErrorResponseInterface;

/**
 * Class ErrorResponse
 * @package Mageplaza\PdfInvoice\Model\Api\Data
 */
class ErrorResponse extends AbstractExtensibleModel implements ErrorResponseInterface
{

    /**
     * @inheritDoc
     */
    public function getMessage()
    {
        return $this->getData(self::MESSAGE);
    }

    /**
     * @inheritDoc
     */
    public function setMessage($message)
    {
        return $this->setData(self::MESSAGE, $message);
    }
}
