<?phpnamespace MyFatoorah\Myfatoorah\Model\Account;class AccountManagement extends \Magento\Customer\Model\AccountManagement {    public function isEmailAvailable($customerEmail, $websiteId = null) {        $objectManager = \Magento\Framework\App\ObjectManager::getInstance();        $cart = $objectManager->get('\Magento\Checkout\Model\Cart');        $shippingAddress = $cart->getQuote()->getShippingAddress();        $billingAddress = $cart->getQuote()->getBillingAddress();        $writer = new \Zend\Log\Writer\Stream(BP . '/var/log/guestFriendsFamily.log');        $logger = new \Zend\Log\Logger();        $logger->addWriter($writer);        $logger->info('------------');        try {            if ($shippingAddress) {                $shippingAddress->setData('email', $customerEmail);                $shippingAddress->save();            }            if ($billingAddress) {                $billingAddress->setData('email', $customerEmail);                $billingAddress->save();            }        } catch (NoSuchEntityException $e) {            $logger->info($e->getMessage());        }        $CustomerModel = $objectManager->create('Magento\Customer\Model\Customer');        $storeManager = $objectManager->get('\Magento\Store\Model\StoreManagerInterface');                $CustomerModel->setWebsiteId($storeManager->getWebsite()->getWebsiteId());         $CustomerModel->loadByEmail($customerEmail);        $userId = $CustomerModel->getId();                if ($userId) {            return false;        } else {            return true;        }    }}?>